mapper.select:
select max(reg_date) as reg_date
from vocabulary.voc_word
where user_id = #{userId}
and reg_date  <  ( select max(reg_date) from vocabulary.voc_word where user_id = #{userId});

select max(reg_date) as reg_date
from vocabulary.voc_word
where user_id = #{userId}
and reg_date  <  (
    select max(reg_date)
    from vocabulary.voc_word
    where user_id = #{userId}
    and reg_date  <  ( select max(reg_date) from vocabulary.voc_word where user_id = #{userId})
);
