mapper.select:
select max(reg_date) as reg_date
from vocabulary.voc_dic
where user_id = #{id};

select max(reg_date) as reg_date
from vocabulary.voc_dic
where user_id = #{id}
and reg_date  <  ( select max(reg_date) from vocabulary.voc_dic where user_id = #{id});

select max(reg_date) as reg_date
from vocabulary.voc_dic
where user_id = #{id}
and reg_date  <  (
    select max(reg_date)
    from vocabulary.voc_dic
    where user_id = #{id}
    and reg_date  <  ( select max(reg_date) from vocabulary.voc_dic where user_id = #{id})
);
